export function useCount() {
  const count = ref(1);
  const getDoubleCount = computed(() => count.value);

  function addCount() {
    count.value++;
  }

  onMounted(() => {
    console.log("useCount is mounted.");
  });

  return { count, getDoubleCount, addCount };
}

